import React, { memo } from "react";
import { useState } from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import Sidebar from "../components/sidebar.tss";
import Important from "../components/important.tsx";
import AddTask from "../components/addTask.jtss";
import Infobar from "../components/infobar.tss";
import General from "../components/General.tsx";
import Planned from "../components/Planned.tsx";

function Dashboard() {
  const [listobj, setList] = useState({});
  const [accountObj, setAccount] = useState({
    userId: "-1",
    accountName: "",
    emailId: "",
    profilePic: "sidebar-tempProfilepic.png",
  });
  const [dataOBj, setdataobj] = useState({
    tasks: [
      {
        name: "new task",
        dueDate: new Date(),
        important: true,
        repeat: true,
        completed: true,
        taskID: "1",
      },
    ],
  });
  const [importantobj, setallTask] = useState({
    tasks: [
      {
        name: "new task",
        dueDate: new Date(),
        important: true,
        repeat: true,
        completed: true,
        taskID: "1",
      },
    ],
  });
  const [infobarObj, setinfobar] = useState({
    genClass: "infoBar infobar-general",
    name: "",
    steps: [""],
    remind: null,
    dueDate: null,
    repeat: null,
    note: "",
    createdOn: null,
    important: false,
  });
  return (
    <>
      <Sidebar
        genClass="sidebar-general"
        userLists={listobj}
        accountInfo={accountObj}
      />
      <div className="mainContent-general">
        <div className="mainContent-general-tasklist">
          <BrowserRouter>
            <Routes>
              <Route
                path="/app/dashboard/myday"
                element={<General {...dataOBj} />}
              />
              <Route
                path="/app/dashboard/important"
                element={<Important {...dataOBj} />}
              ></Route>
              <Route
                path="/app/dashboard/planned"
                element={<Planned {...allTaskobj} />}
              ></Route>
              <Route
                path="/app/dashboard/tasks"
                element={<General {...allTaskobj} />}
              ></Route>
              <Route
                path="/app/dashboard/lists/:id"
                element={<General {...allTaskobj} />}
              ></Route>
            </Routes>
          </BrowserRouter>
        </div>
        <div className="mainContent-general-addTask">
          <AddTask />
        </div>
      </div>
      <Infobar {...infobarObj} />
    </>
  );
}

export default memo(Dashboard);
