import React, { memo, useEffect } from "react";
import { useState } from "react";
import { Route, Routes, useNavigate } from "react-router-dom";
import Sidebar from "../components/sidebar.tsx";
import Important from "../components/important.tsx";
import AddTask from "../components/addTask.tsx";
import Infobar from "../components/infobar.tsx";
import General from "../components/General.tsx";
import Planned from "../components/Planned.tsx";

function Dashboard({ state, updatestate }) {
  const navigate = useNavigate();
  const [listobj, setList] = useState({});
  const [accountObj, setAccount] = useState({
    userId: "-1",
    accountName: "",
    emailId: "",
    profilePic: "sidebar-tempProfilepic.png",
  });
  const [dataOBj, setdataobj] = useState({
    tasks: [
      {
        name: "new task",
        dueDate: new Date(),
        important: true,
        repeat: true,
        completed: true,
        taskID: "1",
      },
    ],
  });
  const [infobarObj, setinfobar] = useState({
    genClass: "infoBar infobar-general",
    name: "",
    steps: [""],
    remind: null,
    dueDate: null,
    repeat: null,
    note: "",
    createdOn: null,
    important: false,
  });
  const verifySession = async () => {
    try {
      const response = await fetch("api/verifyuser");
      if (response.ok) {
        updatestate(true);
      } else {
        updatestate(false);
        navigate("/");
      }
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    verifySession();
  }, []);
  return (
    <>
      <Sidebar
        genClass="sidebar-general"
        userLists={listobj}
        accountInfo={accountObj}
      />
      <div className="mainContent-general">
        <div className="mainContent-general-tasklist">
          <Routes>
            <Route path="/myday" element={<General {...dataOBj} />} />
            <Route path="/important" element={<Important {...dataOBj} />} />
            <Route path="/planned" element={<Planned {...dataOBj} />}></Route>
            <Route path="/tasks" element={<General {...dataOBj} />}></Route>
            <Route path="/lists/:id" element={<General {...dataOBj} />}></Route>
          </Routes>
        </div>
        <div className="mainContent-general-addTask">
          <AddTask />
        </div>
      </div>
      <Infobar {...infobarObj} />
    </>
  );
}

export default memo(Dashboard);
