/* For page Planned task*/
/*contains
earlier ,today ,later
*/
import React, { useState } from "react";
import "../CSS/alltasks.css";
import "../App.css";
import { Task } from "../components/task.tsx";

interface taskprop {
  name: String;
  dueDate: Date;
  important: boolean;
  repeat: boolean;
  completed: boolean;
  taskID: String;
}

interface importantProp {
  ID: string;
}

export function Important({ ID }: importantProp) {
  const plannedIcon = require("../Images/sidebar-box.png");

  const [allTask, setallTask] = useState({
    allTask: [],
  });

  return (
    <div className="alltask">
      <div className="alltask-heading">
        <img src={plannedIcon} alt="planned"></img>
        <div>Important</div>
      </div>
      <br />
      <br />
      <div className="alltask-parts">
        <div>
          {allTask.map((obj, index) => (
            <>
              <Task {...obj} />
              <br />
            </>
          ))}
        </div>
      </div>
      <br />
      <br />
    </div>
  );
}
