/* For page Planned task*/
/*contains
earlier ,today ,later
*/
import React, { memo } from "react";
import "../CSS/alltasks.css";
import "../App.css";
import Task from "../components/task.tsx";

interface taskprop {
  name: String;
  dueDate: Date;
  important: boolean;
  repeat: boolean;
  completed: boolean;
  taskID: String;
}

interface importantProp {
  tasks: taskprop[];
}

function Important({ tasks }: importantProp) {
  const plannedIcon = require("../Images/sidebar-box.png");
  return (
    <div className="alltask">
      <div className="alltask-heading">
        <img src={plannedIcon} alt="planned"></img>
        <div>Important</div>
      </div>
      <br />
      <br />
      <div className="alltask-parts">
        <div>
          {tasks.map((obj, index) => (
            <>
              <Task {...obj} />
              <br />
            </>
          ))}
        </div>
      </div>
      <br />
      <br />
    </div>
  );
}

export default memo(Important);
