import React, { useState } from "react";
import "../CSS/task.css";
import "../App.css";

interface taskProp {
  name: String;
  dueDate: Date;
  important: Boolean;
  repeat: Boolean;
  completed: Boolean;
  taskID: String;
}

export function Task({
  name,
  dueDate,
  important,
  repeat,
  completed,
  taskID,
}: taskProp) {
  const days = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
  const months = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Nov",
    "Dec",
  ];
  const tickimage = require("../Images/task-tick.png");
  const starimage = require("../Images/task-star.png");
  const starONimage = require("../Images/task-star-On.png");
  const repeatImage = require("../Images/task-repeat.png");
  const [taskObj, setTask] = useState({
    name: "",
    dueDate: new Date(),
    important: false,
    repeat: false,
    completed: false,
    taskID: "",
  });
  return (
    <div className="task task-general">
      <div className="task-first-container">
        <div className={completed ? "tick" : "tick tickOff"}>
          {completed ? <img src={tickimage} alt="tick" /> : ""}
        </div>
        <div>
          <div className="task-first-container-name">{name}</div>
          <div className="task-first-container-date">
            {dueDate
              ? `${days[dueDate.getDay()]}, ${dueDate.getDate()} ${
                  months[dueDate.getMonth()]
                }`
              : ""}
            {repeat ? <img src={repeatImage} alt="repeat" /> : ""}
          </div>
        </div>
      </div>
      <div className="task-star">
        <img src={important ? starONimage : starimage} alt="star" />
      </div>
    </div>
  );
}
