/*for Pages : 
lists, myday, alltasks, important
*/
/*contains:
not completed, completed
*/

/* For page Planned task*/
/*contains
earlier ,today ,later
*/
import React, { memo } from "react";
import "../CSS/alltasks.css";
import "../App.css";
import Task from "../components/task.tsx";

interface taskprop {
  name: String;
  dueDate: Date;
  important: boolean;
  repeat: boolean;
  completed: boolean;
  _id: String;
}

interface importantProp {
  tasks: taskprop[];
  setinfobar: Function;
}

function Important({ tasks, setinfobar }: importantProp) {
  const plannedIcon = require("../Images/sidebar-box.png");
  const threedots = require("../Images/infobar-threedots.png");
  return (
    <div className="alltask">
      <div className="alltask-heading">
        <div>
          <img src={plannedIcon} alt="planned"></img>
          <div>Important</div>
        </div>
        <div>
          <div>
            <img
              src={threedots}
              alt="options"
              onClick={() => {
                optionmenu ? openoptionmenu(false) : openoptionmenu(true);
              }}
            ></img>
          </div>
          <div className={`alltaskmenu ${optionmenu ? "optionMenu" : ""}`}>
            <div>
              <div>
                <div>Delete List</div>
              </div>
            </div>
            <div>
              <div>
                <div>Change Name</div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <br />
      <br />
      <div className="alltask-parts">
        <div>
          {tasks.map((obj, index) => (
            <>
              <div
                onClick={() => {
                  setinfobar(obj._id);
                }}
              >
                <Task {...obj} />
              </div>
              <br />
            </>
          ))}
        </div>
      </div>
      <br />
      <br />
    </div>
  );
}

export default memo(Important);
