import React, { memo, useEffect } from "react";
import { useState } from "react";
import {
  Route,
  Routes,
  useLocation,
  useNavigate,
  useParams,
} from "react-router-dom";
import Sidebar from "../components/sidebar.tsx";
import Important from "../components/important.tsx";
import AddTask from "../components/addTask.tsx";
import Infobar from "../components/infobar.tsx";
import General from "../components/General.tsx";
import Planned from "../components/Planned.tsx";

function Dashboard({ state, updatestate }) {
  const location = useLocation();
  const id = useParams();
  const getListname = async () => {
    try {
      const response = await fetch("api/getLists");
      if (response.ok) {
        return await response.json();
      } else {
        return {};
      }
    } catch (err) {
      console.log(err);
    }
  };
  const getAccountinfo = async () => {
    try {
      const response = await fetch("api/getUser");
      if (response.ok) {
        return await response.json();
      } else {
        return {};
      }
    } catch (err) {
      console.log(err);
    }
  };
  const getData = async () => {
    try {
      const match = location.pathname.match(/\/dashboard\/(.*)/);
      const page = match ? match[1] : null;
      const idmatch = location.pathname.match(/\/dashboard\/lists\/([^/]+)/);
      const id = idmatch ? idmatch[1] : null;
      if (page === "important") {
        let response = await fetch("api/getimportant");
        response = await response.json();
        setimportant({
          tasks: response,
        });
      } else if (page === "myday") {
        let response = await fetch("api/getmyday");
        response = await response.json();
        setgeneral(response);
      } else if (page === "planned") {
        let response = await fetch("api/getplanned");
        response = await response.json();
        setplanned(response);
      } else if (page === "tasks") {
        let response = await fetch("api/gettasks");
        response = await response.json();
        setgeneral(response);
      } else if (id) {
        try {
          const response = await fetch("api/getlistdata", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({ id: id }),
          });

          if (response.ok) {
            const data = await response.json();
            setgeneral(data);
          } else {
            console.log("Request failed with status:", response.status);
          }
        } catch (err) {
          console.log(err);
        }
      }
    } catch (err) {
      console.log(err);
    }
  };

  const [changedashboard, setchangedashboard] = useState(false);
  const navigate = useNavigate();
  const [listobj, setList] = useState({});
  const [accountObj, setAccount] = useState({
    userId: "-1",
    accountName: "",
    emailId: "",
    profilepic: "sidebar-tempProfilepic.png",
  });
  const [generaldata, setgeneral] = useState({
    incomplete: [],
    completed: [],
  });
  const [importantdata, setimportant] = useState({
    tasks: [],
  });
  const [planneddata, setplanned] = useState({
    today: [],
    tomorrow: [],
    previous: [],
  });
  const [infobarObj, setinfobar] = useState({
    genClass: "infoBar infobar-general",
    name: "",
    steps: [""],
    remind: null,
    dueDate: null,
    repeat: null,
    note: "",
    createdOn: null,
    important: false,
  });
  const verifySession = async () => {
    try {
      const response = await fetch("/api/verifyuser");
      if (response.ok) {
        updatestate(true);
      } else {
        updatestate(false);
        navigate("/");
      }
    } catch (err) {
      console.log(err);
    }
  };

  const triggerdashboard = () => {
    verifySession();
    getListname().then((res) => {
      setList(res);
    });
    getAccountinfo().then((res) => {
      setAccount(res);
    });
    getData();
  };

  useEffect(() => {
    triggerdashboard();
  }, [id, location]);
  return (
    <>
      <Sidebar
        genClass="sidebar-general"
        userLists={listobj}
        accountInfo={accountObj}
        trigger={changedashboard}
        settrigger={setchangedashboard}
      />
      <div className="mainContent-general">
        <div className="mainContent-general-tasklist">
          <Routes>
            <Route path="/myday" element={<General {...generaldata} />} />
            <Route
              path="/important"
              element={<Important {...importantdata} />}
            />
            <Route
              path="/planned"
              element={<Planned {...planneddata} />}
            ></Route>
            <Route path="/tasks" element={<General {...generaldata} />}></Route>
            <Route
              path="/lists/:id"
              element={<General {...generaldata} />}
            ></Route>
          </Routes>
        </div>
        <div className="mainContent-general-addTask">
          <AddTask />
        </div>
      </div>
      <Infobar {...infobarObj} />
    </>
  );
}

export default memo(Dashboard);
